import React, { useEffect, useState } from 'react';
import './App.css';
import { useWeb3React } from '@web3-react/core'
import { Injected } from './connectors'
import { getContract } from './utils/getContract'
import { MasterChef, ScamCoin, RedDotToken, } from './utils/constant'
const { parseUnits } = require("ethers/lib/utils");


function App() {
  let [userAccount, setUserAccount] = useState<any>('')
  let [samBalance, setSamBalance] = useState<any>(0)

  const { activate, account } = useWeb3React();
  const connectWallet = async () => {
    await activate(Injected)
  }

  const getSamDepositBalance = async () => {
    const contract = await getContract('MSC')
    if (contract != null) {
      let balance = await contract.getUserAmountDeposit(1)
      setSamBalance(parseInt(balance.toString()) / 10 ** 12)
    } else {
      alert('something went wrong')
    }
  }

  const depositSam = async () => {
    const mscContract = await getContract('MSC')
    const samContract = await getContract('SAM')
    if (mscContract != null && samContract != null) {
      const approveSuccess = await samContract.approve(MasterChef.contractAddress, parseUnits("200", 12))
      if (approveSuccess) {
        await mscContract.deposit(1, parseUnits("10", 12))
        alert('deposit roi')
      }
    }
  }
  useEffect(() => {
    if (userAccount) {
      getSamDepositBalance()
    }
  }, [userAccount])
  useEffect(() => {
    if (account) {
      setUserAccount(account)
    }
  }, [account])

  return (
    <div style={{ padding: 20 }}>
      <main style={{ minHeight: 200, padding: 50, flex: 1, flexDirection: 'column', justifyContent: 'center', alignItems: 'center' }}>
        <h1 style={{ color: 'blue' }}>
          collect RDX by staking SAM Coin
        </h1>
        <p>
          Your account: {userAccount}
        </p>

        <button type="button" onClick={connectWallet} style={{ marginTop: 20 }} >
          <p>
            Connect Wallet
          </p>
        </button>
        <h3>
          Your balance Sam in Pool {samBalance} SAM
        </h3>

        <div style={{ flexDirection: 'row', alignItems: 'flex-start', justifyContent: 'flex-start', width: '40%' }}>
          <p> Approve amount of Sam to deposit</p>
          <input style={{ marginRight: 10 }} />
          <button type="button" onClick={() => { console.log('nothing yet') }} >
            <p>Submit</p>
          </button>
          <h3>
            Your balance Sam in Pool {samBalance} SAM
          </h3>
        </div>
        <div style={{ flexDirection: 'row', alignItems: 'flex-start', justifyContent: 'flex-start', width: '40%' }}>
          <p > Deposit Sam:</p>
          <input style={{ marginRight: 10 }} />
          <button type="button" onClick={depositSam} >
            <p>Submit</p>
          </button>
        </div>
        <div style={{ flexDirection: 'row', alignItems: 'flex-start', justifyContent: 'flex-start', width: '40%' }}>
          <p > Withdraw Sam:</p>
          <input style={{ marginRight: 10 }} />
          <button type="button" onClick={() => { console.log('nothing yet') }} >
            <p>Submit</p>
          </button>
        </div>

        <h2>
          Your Pending Reddot token reward: {0} RDX
        </h2>
        <div style={{ flexDirection: 'row', alignItems: 'flex-start', justifyContent: 'flex-start', width: '40%' }}>
          <p > claim reward (make sure your balance is sufficient): </p>
          <input style={{ marginRight: 10 }} />
          <button type="button" onClick={() => { console.log('nothing yet') }} >
            <p>Submit</p>
          </button>
        </div>
      </main>


    </div>
  );
}

export default App;
