import React, { useEffect, useState } from 'react';
import './App.css';
import { useWeb3React } from '@web3-react/core'
import { Injected } from './connectors'
import { getContract } from './utils/getContract'

function App() {
  // let [ropstenChainId, setRopstenChainId] = useState<any>(0)
  let [userAccount, setUserAccount] = useState<any>('')
  let [samBalance, setSamBalance] = useState<any>(0)

  const { activate, deactivate, chainId, account } = useWeb3React();
  const connectWallet = async () => {
    await activate(Injected)
    setUserAccount(account)
    // setRopstenChainId(chainId);
  }

  const getSamBalance = async () => {
    const contract = getContract('SAM')
    if (contract != null) {
      const balance = await contract.balanceOf(userAccount)
      debugger

      setSamBalance(balance)
      window.location.reload();
    } else {
      alert('something went wrong')
    }
  }
  useEffect(() => {
    getSamBalance()
  })

  return (
    <div style={{ padding: 20 }}>
      <main style={{ minHeight: 200, padding: 50, flex: 1, flexDirection: 'column', justifyContent: 'center', alignItems: 'center' }}>
        <h1 style={{ color: 'blue' }}>
          collect RDX by staking SAM Coin
        </h1>
        <p>
          Your account: {userAccount}
        </p>

        <button type="button" onClick={connectWallet} style={{ marginTop: 20 }} >
          <p>
            Connect Wallet
          </p>
        </button>
        <h2>
          Your Sam deposit balance: {samBalance} SAM
        </h2>

        <div style={{ flexDirection: 'row', alignItems: 'flex-start', justifyContent: 'flex-start', width: '40%' }}>
          <p> Approve spending</p>
          <input style={{ marginRight: 10 }} />
          <button type="button" onClick={() => { console.log('nothing yet') }} >
            <p>Submit</p>
          </button>
        </div>
        <div style={{ flexDirection: 'row', alignItems: 'flex-start', justifyContent: 'flex-start', width: '40%' }}>
          <p > Deposit Sam:</p>
          <input style={{ marginRight: 10 }} />
          <button type="button" onClick={() => { console.log('nothing yet') }} >
            <p>Submit</p>
          </button>
        </div>
        <div style={{ flexDirection: 'row', alignItems: 'flex-start', justifyContent: 'flex-start', width: '40%' }}>
          <p > Withdraw Sam:</p>
          <input style={{ marginRight: 10 }} />
          <button type="button" onClick={() => { console.log('nothing yet') }} >
            <p>Submit</p>
          </button>
        </div>

        <h2>
          Your Pending Reddot token reward: {0} RDX
        </h2>
        <div style={{ flexDirection: 'row', alignItems: 'flex-start', justifyContent: 'flex-start', width: '40%' }}>
          <p > claim reward (make sure your balance is sufficient): </p>
          <input style={{ marginRight: 10 }} />
          <button type="button" onClick={() => { console.log('nothing yet') }} >
            <p>Submit</p>
          </button>
        </div>
      </main>


    </div>
  );
}

export default App;
